function Summarizer() {
    return (
        <div className="App">
            <main>
                <div class="Summarizer-Left">
                    <Prompt />
                </div>
                <div class="Summarizer-Right">
                    <Response onSubmit={handleNewEntry} />
                </div>
            </main>
        </div>
    );
}

function Prompt(){
    return (
        <div>
        <textarea 
            ref={txtUserMessage} 
            value={text}
            onKeyDown={handleKeyDown}
            onChange={(e) => setText(e.target.value)}
            maxLength="200px"
            placeholder="Ask me something, and press Enter"
            />
        </div>
    );
}

