import './App.css';
import React, {useRef, useEffect, useState} from 'react';

function App() {
  const [entries, setEntries] = useState([]);

  const handleNewEntry = async (text) => {
    if (text.trim() !== '') {
      // First: display user message:
      setEntries((prev) => [...prev, [text, 'userMsg']]);

      // Temporarily, display "..." for AI message (optional)
      //setEntries((prev) => [...prev, ["...", 'AIMsg']]);
      try {
        const res = await fetch('http://localhost:5000/api/chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message: text })
        });
        const data = await res.json();
        // Retrieve AI message sent back:
        const response = data.choices[0].message.content;
        // Print message on console:
        console.log(response);
        // Print on UI client:
        setEntries((prev) => [...prev, [response, 'AIMsg']]);
        entries[entries.length - 1].value = "Alterado";

      } catch (error) {
        console.error('Error calling OpenAI:', error);
      }
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <Header />
      </header>
      <main className="App-main">
        <Main entries={entries} />
      </main>
      <footer className="App-footer">
        <Footer onSubmit={handleNewEntry} />
      </footer>
    </div>
  );
}
function Main({ entries }){
  return (
    <div class="messages">
      {
        entries.map((entry, index) => (
          <div key={index} class={entry[1]}>
            {entry[0]}
          </div>
        ))
      }
    </div>
  );
}

function Footer({ onSubmit }){
  const txtUserMessage = useRef(null);
  const [text, setText] = useState('');

  useEffect(() => {
    txtUserMessage.current?.focus(); // Focus the input when component mounts
  }, []);

  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault(); // Prevent newline
      console.log('Typed text: ' + text);
      onSubmit(text, 'userMsg'); // Call
      setText(''); // Clear textarea text
      txtUserMessage.current?.focus(); // Focus the input again
    }
  }
  return (
    <div>
      <textarea 
        ref={txtUserMessage} 
        value={text}
        onKeyDown={handleKeyDown}
        onChange={(e) => setText(e.target.value)}
        maxLength="200px"
        placeholder="Ask me something, and press Enter"
         />
    </div>
  );
}

function Header(){
  return (
    <ul class="nav">
      <li><a href="">Chat</a></li>
      <li><a href="">Web Summerizer</a></li>
    </ul>
  );
}

export default App;
