import React, {useRef, useEffect, useState} from 'react';
import * as cheerio from "cheerio"

// async function getTextFromPage(url) {
//     console.log(`Em getTextFromPage(), url = ${url}`);
//     const res = await fetch(url);
//     const html = await res.text();
//     const $ = cheerio.load(html);
//     return $("body").text();
// }

async function getTextFromPage(webPageURL) {
    try{
        const response = await fetch(`http://localhost:5000/api/gettextfrompage/${webPageURL}`);
        const text = await response.text();

        return text;
    } catch (error) {
        console.error('Error calling Back-end:', error);
    }
    /*const res = await fetch('http://localhost:5000/api/gettextfrompage', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url: webPageURL })
    });
    const data = await res.json();
    console.log(data.response);
    return data.response;*/
}

async function insertEntry(text, [entries, setEntries]){
    // Display user typed message:
    setEntries((prev) => [...prev, [text, 'userMsg']]);
    try {
        const pageText = getTextFromPage(text);

        const prompt = [
            { role: 'system', content: '' },
            { role: 'user', content: pageText}
        ]

        const res = await fetch('http://localhost:5000/api/chat', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ message: prompt })
        });

        console.log(res);

        const data = await res.json();
        // Retrieve AI message sent back:
        //const response = data.choices[0].message.content; // for ChatGPT using OpenAI
        const response = data.response; //just for ollama
        // console.log(response); // Print message on console
        // Print on UI client:
        setEntries((prev) => [...prev, [response, 'AIMsg']]);
    } catch (error) {
        console.error('Error calling Back-end:', error);
    }
    finally{
        // nothing
    }
}

function Chat() {
  const [entries, setEntries] = useState([]);
 
  const handleNewEntry = async (text) => {
    if (text.trim() !== '') {
      insertEntry(text, [entries, setEntries]);
    }
  };

  return (
      <div className="App">
        <main className="Chat-main">
          <Main entries={entries} />
        </main>
        <footer className="Chat-footer">
          <Footer onSubmit={handleNewEntry} />
        </footer>
      </div>
  );
}

function Main({ entries }){
  return (
    <div class="messages">
      {
        entries.map((entry, index) => (
          <div key={index} class={entry[1]}>
            {entry[0]}
          </div>
        ))
      }
    </div>
  );
}

function Footer({ onSubmit }){
  const txtUserMessage = useRef(null);
  const [text, setText] = useState('');

  useEffect(() => {
    txtUserMessage.current?.focus(); // Focus the input when component mounts
  }, []);

  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault(); // Prevent newline
      //console.log('Typed text: ' + text);
      onSubmit(text, 'userMsg'); // Call
      setText(''); // Clear textarea content
      txtUserMessage.current?.focus(); // Focus the input again
    }
  }
  return (
    <div>
        <input
            type="text" 
            ref={txtUserMessage} 
            value={text}
            onKeyDown={handleKeyDown}
            onChange={(e) => setText(e.target.value)}
            maxLength="200px"
            placeholder="Type the website URL and press Enter"
        />
    </div>
  );
}
export default Chat;
